mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID = #{checkId}

mapper.update:
UPDATE MEMBER
SET USER_PWD = #{userPwd}
WHERE USER_ID = #{userId}

mapper.select:
SELECT COUNT(*)
  FROM MEMBER
 WHERE USER_NICKNAME = #{checkNickname}

mapper.update:
UPDATE MEMBER
   SET USER_PROFILE = #{userProfile}
     , USER_NICKNAME = #{userNickname}
 WHERE USER_NO = #{userNo}

UPDATE MEMBER
   SET USER_PWD = '000000'
 WHERE USER_NO = #{userNo}

UPDATE MEMBER
   SET USER_STATUS = 'N'
 WHERE USER_NO = #{userNo}
